import mathwhile True:    print(        "\nChoose the math operation.\n\n0 - Addition\n1 - Subtraction\n2 - Multiplication\n3 - Division\n4 - Modulo\n5 - Raising to a power\n6 - Square root\n7 - Logarithm\n8 - Sine\n9 - Cosine\n10 - Tangent\n")    operation = input("\nYour option from the menu: ")    # Addition    if operation == "0":        val1 = float(input("\nFirst value: "))        val2 = float(input("\nSecond value: "))        print("\nThe result is: " + str(val1 + val2) + "\n")        back = input('\nGo back to the main menu? (y/n) ')        if back == 'y':            continue        else:            print("Goodbye!")            break    # Subtraction    elif operation == "1":        val1 = float(input("\nFirst value: "))        val2 = float(input("\nSecond value: "))        print("\nThe result is: " + str(val1 - val2) + "\n")        back = input('\nGo back to the main menu? (y/n) ')        if back == 'y':            continue        else:            print("Goodbye!")            break    # Multiplication    elif operation == "2":        val1 = float(input("\nFirst value: "))        val2 = float(input("\nSecond value: "))        print("\nThe result is: " + str(val1 * val2) + "\n")        back = input('\nGo back to the main menu? (y/n) ')        if back == 'y':            continue        else:            print("Goodbye!")            break    # Division    elif operation == "3":        val1 = float(input("\nFirst value: "))        val2 = float(input("\nSecond value: "))        print("\nThe result is: " + str(val1 / val2) + "\n")        # Going back to the main menu or exiting the program        back = input('\nGo back to the main menu? (y/n) ')        if back == 'y':            continue        else:            print("Goodbye!")            break    # Modulo    elif operation == "4":        val1 = float(input("\nFirst value: "))        val2 = float(input("\nSecond value: "))        print("\nThe result is: " + str(val1 % val2) + "\n")        # Going back to the main menu or exiting the program        back = input('\nGo back to the main menu? (y/n) ')        if back == 'y':            continue        else:            print("Goodbye!")            break    # Raising to a power    elif operation == "5":        val1 = float(input("\nEnter the base: "))        val2 = float(input("\nEnter the power: "))        print("\nThe result is: " + str(math.pow(val1, val2)) + "\n")        # Going back to the main menu or exiting the program        back = input('\nGo back to the main menu? (y/n) ')        if back == 'y':            continue        else:            print("Goodbye!")            break    # Square root    elif operation == "6":        val1 = float(input("\nEnter value for extracting the square root: "))        print("\nThe result is: " + str(math.sqrt(val1)) + "\n")        # Going back to the main menu or exiting the program        back = input('\nGo back to the main menu? (y/n) ')        if back == 'y':            continue        else:            print("Goodbye!")            break    # Logarithm    elif operation == "7":        val1 = float(input("\nEnter the value for calculating the logarithm to base 2: "))        print("\nThe result is: " + str(math.log(val1, 2)) + "\n")        # Going back to the main menu or exiting the program        back = input('\nGo back to the main menu? (y/n) ')        if back == 'y':            continue        else:            print("Goodbye!")            break    # Sine    elif operation == "8":        val1 = float(input("\nEnter the value (in degrees) for calculating the sine: "))        print("\nThe result is: " + str(math.sin(math.radians(val1))) + "\n")        back = input('\nGo back to the main menu? (y/n) ')        if back == 'y':            continue        else:            print("Goodbye!")            break    # Cosine    elif operation == "9":        val1 = float(input("\nEnter the value (in degrees) for calculating the cosine: "))        print("\nThe result is: " + str(math.cos(math.radians(val1))) + "\n")        back = input('\nGo back to the main menu? (y/n) ')        if back == 'y':            continue        else:            print("Goodbye!")            break    # Tangent    elif operation == "10":        val1 = float(input("\nEnter the value (in degrees) for calculating the tangent: "))        print("\nThe result is: " + str(math.tan(math.radians(val1))) + "\n")        back = input('\nGo back to the main menu? (y/n) ')        if back == 'y':            continue        else:            break    else:        print("\nInvalid Value\n")        continue        # End of the program